  email: 'john@example.com',
  phone: '+1234567890',
  resumeUrl: 'https://example.com/resume.pdf',
  coverLetter: 'I am interested in this position because...',
  status: 'pending',
  submittedAt: new Date(),
  reviewedAt: null,
  notes: null,
  viewed: false,
  downloaded: false,
