import { describe, it, expect, beforeAll, afterAll } from 'vitest';
import request from 'supertest';
import express from 'express';
import { registerRoutes } from '../../server/routes';
import { createMockJob, createMockUser, createMockApplication } from '../factories';

let app: express.Express;
let server: any;

beforeAll(async () => {
  app = express();
  server = await registerRoutes(app);
});

afterAll(() => {
  server?.close();
});

describe('API Integration Tests', () => {
  describe('Jobs API', () => {
    it('retrieves jobs list with proper pagination', async () => {
      const response = await request(app)
        .get('/api/jobs')
        .query({ page: 1, limit: 10 });

      expect([200, 401]).toContain(response.status);

      if (response.status === 200) {
        expect(Array.isArray(response.body)).toBe(true);
        expect(response.body.length).toBeLessThanOrEqual(10);
      }
    });

    it('filters jobs by search criteria', async () => {
      const response = await request(app)
        .get('/api/jobs')
        .query({ search: 'developer', location: 'Remote' });

      expect([200, 401]).toContain(response.status);

      if (response.status === 200) {
        expect(Array.isArray(response.body)).toBe(true);
      }
    });

    it('validates job posting schema', async () => {
      const mockJob = createMockJob();

      const response = await request(app)
        .post('/api/jobs')
        .send(mockJob);

      // Should require authentication
      expect([400, 401, 403]).toContain(response.status);
    });

    it('retrieves individual job details', async () => {
      const response = await request(app).get('/api/jobs/1');

      expect([200, 404]).toContain(response.status);

      if (response.status === 200) {
        expect(response.body).toHaveProperty('id');
        expect(response.body).toHaveProperty('title');
        expect(response.body).toHaveProperty('description');
      }
    });
  });

  describe('Applications API', () => {
    it('validates application submission data', async () => {
      const mockApplication = createMockApplication();

      const response = await request(app)
        .post('/api/jobs/1/apply')
        .send(mockApplication);

      // Expect validation or authentication error
      expect([400, 401, 403]).toContain(response.status);
    });

    it('handles file upload for resumes', async () => {
      const mockPdfBuffer = Buffer.from('%PDF-1.4 mock content');

      const response = await request(app)
        .post('/api/jobs/1/apply')
        .field('name', 'Test User')
        .field('email', 'test@example.com')
        .field('phone', '+1234567890')
        .attach('resume', mockPdfBuffer, 'resume.pdf');

      expect([200, 400, 401]).toContain(response.status);
    });

    it('retrieves applications for recruiters', async () => {
      const response = await request(app).get('/api/jobs/1/applications');

      // Should require authentication and proper role
      expect([401, 403]).toContain(response.status);
    });
  });

  describe('Authentication API', () => {
    it('handles login endpoint availability', async () => {
      const credentials = {
        username: 'testuser',
        password: 'testpass'
      };

      const response = await request(app)
        .post('/api/login')
        .send(credentials);

      // Authentication endpoint should respond with 400/401 for test credentials
      expect([400, 401]).toContain(response.status);
    });

    it('validates login request format', async () => {
      const invalidRequest = {
        user: 'invalid',
        pass: 'wrong'
      };

      const response = await request(app)
        .post('/api/login')
        .send(invalidRequest);

      expect([400, 401]).toContain(response.status);
    });

    it('handles registration endpoint', async () => {
      const newUser = {
        username: 'newuser',
        email: 'newuser@test.com',
        password: 'password123'
      };

      const response = await request(app)
        .post('/api/register')
        .send(newUser);

      // Registration should handle new user or return validation error
      expect([201, 400, 409]).toContain(response.status);
    });

    it('handles logout endpoint', async () => {
      const response = await request(app).post('/api/logout');
      // Logout should work regardless of session state
      expect([200, 401]).toContain(response.status);
    });

    it('protects user endpoint', async () => {
      const response = await request(app).get('/api/user');
      // User endpoint should require authentication
      expect(response.status).toBe(401);
    });
  });

  describe('Admin API', () => {
    it('protects admin statistics endpoint', async () => {
      const response = await request(app).get('/api/admin/stats');
      expect([401, 403]).toContain(response.status);
    });

    it('protects user management endpoints', async () => {
      const response = await request(app).get('/api/admin/users');
      expect([401, 403]).toContain(response.status);
    });

    it('validates job review operations', async () => {
      const reviewData = {
        status: 'approved',
        comments: 'Job meets quality standards'
      };

      const response = await request(app)
        .patch('/api/admin/jobs/1/review')
        .send(reviewData);

      expect([401, 403]).toContain(response.status);
    });
  });

  describe('AI Analysis API', () => {
    it('validates AI analysis input schema', async () => {
      const validJobData = {
        title: 'Software Engineer',
        description: 'We are looking for a skilled software engineer.'
      };

      const response = await request(app)
        .post('/api/ai/analyze-job-description')
        .send(validJobData);

      expect([200, 401, 403, 429]).toContain(response.status);
    });

    it('handles invalid AI analysis input', async () => {
      const invalidData = {
        title: '', // Empty title
        description: 'x'.repeat(10000) // Too long description
      };

      const response = await request(app)
        .post('/api/ai/analyze-job-description')
        .send(invalidData);

      expect([400, 401, 403]).toContain(response.status);
    });

    it('respects rate limiting for AI requests', async () => {
      const jobData = {
        title: 'Test Job',
        description: 'Test description for rate limiting'
      };

      let rateLimitHit = false;
      const agent = request.agent(app);

      // Make multiple rapid requests
      for (let i = 0; i < 10; i++) {
        const response = await agent
          .post('/api/ai/analyze-job-description')
          .send(jobData);

        if (response.status === 429) {
          rateLimitHit = true;
          break;
        }
      }

      // Rate limiting should eventually kick in
      expect(rateLimitHit).toBe(true);
    });
  });

  describe('Profile API', () => {
    it('protects profile endpoints', async () => {
      const response = await request(app).get('/api/profile');
      expect(response.status).toBe(401);
    });

    it('validates profile update data', async () => {
      const profileData = {
        email: 'newemail@example.com',
        preferences: { notifications: true }
      };

      const response = await request(app)
        .patch('/api/profile')
        .send(profileData);

      expect([401, 403]).toContain(response.status);
    });
  });

  describe('Analytics API', () => {
    it('provides job analytics to authorized users', async () => {
      const response = await request(app).get('/api/analytics/jobs');
      expect([401, 403]).toContain(response.status);
    });

    it('generates analytics exports', async () => {
      const response = await request(app).get('/api/analytics/export');
      expect([401, 403]).toContain(response.status);
    });
  });

  describe('Error Handling', () => {
    it('handles malformed JSON gracefully', async () => {
      const response = await request(app)
        .post('/api/jobs')
        .set('Content-Type', 'application/json')
        .send('{"invalid": json}');

      expect(response.status).toBe(400);
    });

    it('returns appropriate 404 for missing resources', async () => {
      const response = await request(app).get('/api/jobs/999999');
      expect(response.status).toBe(404);
    });

    it('handles database connection errors gracefully', async () => {
      // This would test how the API handles database unavailability
      const response = await request(app).get('/api/jobs');
      expect([200, 401, 500]).toContain(response.status);
    });
  });
});

test/performance/load-test.js.content:
import http from 'k6/http';
import { check, sleep } from 'k6';
import { Rate } from 'k6/metrics';

// Custom metrics
export let errorRate = new Rate('errors');

export let options = {
  stages: [
    { duration: '30s', target: 10 }, // Ramp up to 10 users
    { duration: '1m', target: 10 },  // Stay at 10 users
    { duration: '30s', target: 25 }, // Ramp up to 25 users
    { duration: '1m', target: 25 },  // Stay at 25 users
    { duration: '30s', target: 0 },  // Ramp down to 0 users
  ],
  thresholds: {
    http_req_duration: ['p(95)<3000'], // 95% of requests must complete below 3s
    http_req_failed: ['rate<0.2'],     // Error rate must be below 20%
    errors: ['rate<0.2'],              // Custom error rate below 20%
  },
};

const BASE_URL = 'http://localhost:5000';

export default function () {
  // Test 1: Homepage load
  let response = http.get(`${BASE_URL}/`, { timeout: '10s' });
  let success1 = check(response, {
    'homepage status is valid': (r) => r.status >= 200 && r.status < 500,
    'homepage loads in reasonable time': (r) => r.timings.duration < 2000,
  });
  if (!success1) errorRate.add(1);

  sleep(Math.random() * 2 + 1); // Random sleep 1-3 seconds

  // Test 2: Jobs API endpoint
  response = http.get(`${BASE_URL}/api/jobs`, { timeout: '10s' });
  let success2 = check(response, {
    'jobs API status is acceptable': (r) => r.status === 200 || r.status === 401 || r.status === 403,
    'jobs API responds timely': (r) => r.timings.duration < 3000,
    'jobs API returns valid response': (r) => {
      if (r.status === 401 || r.status === 403) return true;
      try {
        JSON.parse(r.body);
        return true;
      } catch (e) {
        return r.status !== 200; // Only fail if 200 but invalid JSON
      }
    },
  });
  if (!success2) errorRate.add(1);

  sleep(Math.random() * 2 + 1);

  // Test 3: Job search simulation (only run 50% of the time to reduce load)
  if (Math.random() > 0.5) {
    const searchParams = ['developer', 'manager', 'designer', 'analyst'];
    const randomSearch = searchParams[Math.floor(Math.random() * searchParams.length)];

    response = http.get(`${BASE_URL}/api/jobs?search=${randomSearch}`, { timeout: '10s' });
    let success3 = check(response, {
      'search API responds correctly': (r) => r.status === 200 || r.status === 401 || r.status === 403,
      'search completes reasonably': (r) => r.timings.duration < 3000,
    });
    if (!success3) errorRate.add(1);
  }

  sleep(Math.random() * 3 + 1);

  // Test 4: Login simulation (only run 30% of the time)
  if (Math.random() > 0.7) {
    const credentials = {
      username: 'testuser',
      password: 'testpass'
    };

    response = http.post(`${BASE_URL}/api/login`, JSON.stringify(credentials), {
      headers: { 'Content-Type': 'application/json' },
      timeout: '10s'
    });

    let success4 = check(response, {
      'login API responds': (r) => r.status >= 200 && r.status < 500,
      'login responds reasonably': (r) => r.timings.duration < 5000,
    });
    if (!success4) errorRate.add(1);
  }

  sleep(Math.random() * 2 + 1);

  // Test 5: Skip AI Analysis during stress test to prevent overwhelming the server
  // This endpoint is computationally expensive and should be tested separately
}

export function teardown(data) {
  console.log('Performance test completed');
  console.log(`Average response time: ${data.http_req_duration}`);
  console.log(`Error rate: ${data.http_req_failed}`);
}

