import { describe, it, expect, beforeAll, afterAll } from 'vitest';
import request from 'supertest';
import express from 'express';
import { registerRoutes } from '../../server/routes';

let app: express.Express;
let server: any;

beforeAll(async () => {
  app = express();
  server = await registerRoutes(app);
});

afterAll(() => {
  server?.close();
});

describe('Security Tests', () => {
  describe('Authentication & Authorization', () => {
    it('blocks unauthorized access to protected routes', async () => {
      const protectedEndpoints = [
        '/api/jobs',
        '/api/my-jobs',
        '/api/admin/stats',
        '/api/admin/users',
        '/api/profile'
      ];

      for (const endpoint of protectedEndpoints) {
        const response = await request(app).get(endpoint);
        expect([401, 403]).toContain(response.status);
      }
    });

    it('prevents role escalation attacks', async () => {
      // Test candidate trying to access admin routes
      const candidateSession = request.agent(app);

      // Attempt to access admin endpoints without proper role
      const adminResponse = await candidateSession.get('/api/admin/stats');
      expect([401, 403]).toContain(adminResponse.status);

      const userManagementResponse = await candidateSession.get('/api/admin/users');
      expect([401, 403]).toContain(userManagementResponse.status);
    });

    it('validates session security', async () => {
      // Test session fixation prevention
      const agent = request.agent(app);

      const loginResponse = await agent
        .post('/api/login')
        .send({ username: 'testuser', password: 'wrongpassword' });

      expect(loginResponse.status).toBe(401);

      // Verify no session is created for failed login
      const protectedResponse = await agent.get('/api/user');
      expect(protectedResponse.status).toBe(401);
    });
  });

  describe('Input Validation & SQL Injection Prevention', () => {
    it('prevents SQL injection in job search', async () => {
      const sqlInjectionPayloads = [
        "'; DROP TABLE jobs; --",
        "' OR '1'='1",
        "'; SELECT * FROM users; --",
        "' UNION SELECT password FROM users --"
      ];

      for (const payload of sqlInjectionPayloads) {
        const response = await request(app)
          .get('/api/jobs')
          .query({ search: payload });

        // Should not return 500 error or expose database structure
        expect(response.status).not.toBe(500);
        expect(response.text).not.toContain('DROP TABLE');
        expect(response.text).not.toContain('SELECT');
      }
    });

    it('sanitizes job posting inputs', async () => {
      const maliciousJob = {
        title: "<script>alert('xss')</script>",
        description: "'; DROP TABLE applications; --",
        location: "<img src=x onerror=alert('xss')>",
        type: "' OR 1=1 --"
      };

      const response = await request(app)
        .post('/api/jobs')
        .send(maliciousJob);

      // Should reject malicious input or sanitize it
      expect([400, 401, 403]).toContain(response.status);
    });

    it('validates file upload security', async () => {
      // Test malicious file upload attempts
      const maliciousFiles = [
        { filename: 'malware.exe', content: 'MZ\x90\x00' }, // PE header
        { filename: 'script.php', content: '<?php system($_GET["cmd"]); ?>' },
        { filename: '../../../etc/passwd', content: 'root:x:0:0:root' }
      ];

      for (const file of maliciousFiles) {
        const response = await request(app)
          .post('/api/jobs/1/apply')
          .attach('resume', Buffer.from(file.content), file.filename);

        // Should reject non-PDF files and path traversal attempts
        expect([400, 401, 403]).toContain(response.status);
      }
    });
  });

  describe('Rate Limiting & DoS Prevention', () => {
    it('enforces rate limiting on login attempts', async () => {
      const agent = request.agent(app);
      let rateLimitHit = false;

      // Attempt multiple rapid logins
      for (let i = 0; i < 10; i++) {
        const response = await agent
          .post('/api/login')
          .send({ username: 'testuser', password: 'wrongpassword' });

        if (response.status === 429) {
          rateLimitHit = true;
          break;
        }
      }

      expect(rateLimitHit).toBe(true);
    });

    it('enforces rate limiting on job applications', async () => {
      const agent = request.agent(app);
      let rateLimitHit = false;

      // Attempt rapid job applications
      for (let i = 0; i < 20; i++) {
        const response = await agent
          .post('/api/jobs/1/apply')
          .send({
            name: 'Test User',
            email: 'test@example.com',
            phone: '+1234567890'
          });

        if (response.status === 429) {
          rateLimitHit = true;
          break;
        }
      }

      expect(rateLimitHit).toBe(true);
    });

    it('enforces rate limiting on AI analysis requests', async () => {
      const agent = request.agent(app);
      let rateLimitHit = false;

      // Attempt rapid AI analysis requests
      for (let i = 0; i < 10; i++) {
        const response = await agent
          .post('/api/ai/analyze-job-description')
          .send({
            title: 'Test Job',
            description: 'Test description'
          });

        if (response.status === 429) {
          rateLimitHit = true;
          break;
        }
      }

      expect(rateLimitHit).toBe(true);
    });
  });

  describe('Data Exposure Prevention', () => {
    it('does not expose sensitive user data in responses', async () => {
      const response = await request(app).get('/api/jobs');

      if (response.status === 200) {
        const responseText = response.text.toLowerCase();

        // Should not expose password hashes or sensitive data
        expect(responseText).not.toContain('password');
        expect(responseText).not.toContain('hash');
        expect(responseText).not.toContain('salt');
        expect(responseText).not.toContain('secret');
      }
    });

    it('prevents information disclosure in error messages', async () => {
      const response = await request(app)
        .get('/api/nonexistent-endpoint');

      expect(response.status).toBe(404);

      // Should not expose internal paths or system information
      expect(response.text).not.toContain('/server/');
      expect(response.text).not.toContain('node_modules');
      expect(response.text).not.toContain('stack trace');
    });
  });

  describe('CORS & Header Security', () => {
    it('implements proper CORS policies', async () => {
      const response = await request(app)
        .options('/api/jobs')
        .set('Origin', 'https://malicious-site.com');

      // Should not allow arbitrary origins
      expect(response.headers['access-control-allow-origin']).not.toBe('*');
    });

    it('sets security headers', async () => {
      const response = await request(app).get('/');

      // Check for security headers (if implemented)
      const headers = response.headers;

      // These might be set by helmet middleware
      if (headers['x-content-type-options']) {
        expect(headers['x-content-type-options']).toBe('nosniff');
      }

      if (headers['x-frame-options']) {
        expect(headers['x-frame-options']).toMatch(/DENY|SAMEORIGIN/);
      }
    });
  });

  describe('Session Security', () => {
    it('regenerates session IDs on login', async () => {
      const agent = request.agent(app);

      // Get initial session
      const initialResponse = await agent.get('/api/user');
      const initialCookies = initialResponse.headers['set-cookie'];

      // Attempt login (even if it fails)
      await agent.post('/api/login').send({
        username: 'testuser',
        password: 'testpass'
      });

      // Check if session changed
      const postLoginResponse = await agent.get('/api/user');
      const postLoginCookies = postLoginResponse.headers['set-cookie'];

      // Session should be different (if session regeneration is implemented)
      if (initialCookies && postLoginCookies) {
        expect(initialCookies).not.toEqual(postLoginCookies);
      }
    });

    it('properly destroys sessions on logout', async () => {
      const agent = request.agent(app);

      // Attempt logout
      const logoutResponse = await agent.post('/api/logout');

      // Session should be destroyed
      const postLogoutResponse = await agent.get('/api/user');
      expect(postLogoutResponse.status).toBe(401);
    });
  });
});

