        <div className="flex flex-col space-y-6">
          <a 
            href="/#about" 
            className="text-xl relative px-2 py-1 text-white transition-all duration-300 border-l-2 pl-4 border-transparent hover:border-[#7B38FB]"
            onClick={(e) => {
              e.preventDefault();
              setIsMenuOpen(false);
              if (window.location.pathname === '/') {
