}

interface ApplicationWithDetails {
  id: number;
  fullName: string;
  email: string;
  phone: string;
  coverLetter: string;
  status: string;
  appliedAt: string;
  viewedAt?: string;
  downloadedAt?: string;
  notes?: string;
  job: {
    id: number;
    title: string;
    company: string;
  };
  recruiterNotes?: string;
}

interface UserDetails {
  id: number;
  username: string;
  firstName: string;
  lastName: string;
  role: string;
  createdAt: string;
  profile?: {
    bio?: string;
    skills?: string[];
    linkedin?: string;
    location?: string;
  };
