import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useAuth } from "@/hooks/use-auth";
import { Redirect } from "wouter";
import { 
  User, 
  MapPin, 
  Calendar, 
  Eye, 
  Download, 
  Trash2, 
  Edit3, 
  Save, 
  X,
  Plus,
  Briefcase,
  Clock,
  CheckCircle,
  XCircle,
  UserCheck,
  Linkedin,
  Mail,
  Phone,
  Star,
  Target
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { UserProfile, Application, Job } from "@shared/schema";
import { apiRequest, queryClient } from "@/lib/queryClient";
import Layout from "@/components/Layout";

type ApplicationWithJob = Application & { job: Job };

export default function CandidateDashboard() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [editingProfile, setEditingProfile] = useState(false);
  const [profileData, setProfileData] = useState({
    bio: "",
    skills: [] as string[],
    linkedin: "",
    location: "",
  });
  const [newSkill, setNewSkill] = useState("");
  const [isVisible, setIsVisible] = useState(false);

  // Fade-in animation on mount
  useEffect(() => {
    const timer = setTimeout(() => setIsVisible(true), 200);
    return () => clearTimeout(timer);
  }, []);

  // Redirect if not authenticated
  if (!user) {
    return <Redirect to="/auth" />;
  }

  const { data: profile, isLoading: profileLoading } = useQuery<UserProfile | null>({
    queryKey: ["/api/profile"],
    queryFn: async () => {
      const response = await fetch("/api/profile");
      if (!response.ok) throw new Error("Failed to fetch profile");
      return response.json();
    },
  });

  const { data: applications, isLoading: applicationsLoading } = useQuery<ApplicationWithJob[]>({
    queryKey: ["/api/my-applications"],
    queryFn: async () => {
      const response = await fetch("/api/my-applications");
      if (!response.ok) throw new Error("Failed to fetch applications");
      return response.json();
    },
  });

  const updateProfileMutation = useMutation({
    mutationFn: async (data: any) => {
      const method = profile ? "PATCH" : "POST";
      const res = await apiRequest(method, "/api/profile", data);
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/profile"] });
      setEditingProfile(false);
      toast({
        title: "Profile updated",
        description: "Your profile has been updated successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Update failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const withdrawApplicationMutation = useMutation({
    mutationFn: async (applicationId: number) => {
      const res = await apiRequest("DELETE", `/api/applications/${applicationId}/withdraw`);
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/my-applications"] });
      toast({
        title: "Application withdrawn",
        description: "Your application has been withdrawn successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Withdrawal failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleEditProfile = () => {
    setProfileData({
      bio: profile?.bio || "",
      skills: profile?.skills || [],
      linkedin: profile?.linkedin || "",
      location: profile?.location || "",
    });
    setEditingProfile(true);
  };

  const handleSaveProfile = () => {
    updateProfileMutation.mutate(profileData);
  };

  const handleAddSkill = () => {
    if (newSkill.trim() && !profileData.skills.includes(newSkill.trim())) {
      setProfileData({
        ...profileData,
        skills: [...profileData.skills, newSkill.trim()]
      });
      setNewSkill("");
    }
  };

  const handleRemoveSkill = (skill: string) => {
    setProfileData({
      ...profileData,
      skills: profileData.skills.filter(s => s !== skill)
    });
  };

  const handleWithdrawApplication = (applicationId: number) => {
    if (confirm("Are you sure you want to withdraw this application? This action cannot be undone.")) {
      withdrawApplicationMutation.mutate(applicationId);
    }
  };

  const getStatusBadge = (status: string) => {
    const statusConfig = {
      submitted: { color: "bg-blue-500/20 text-blue-300", icon: Clock, label: "Submitted" },
      reviewed: { color: "bg-yellow-500/20 text-yellow-300", icon: Eye, label: "Under Review" },
      shortlisted: { color: "bg-green-500/20 text-green-300", icon: UserCheck, label: "Shortlisted" },
      rejected: { color: "bg-red-500/20 text-red-300", icon: XCircle, label: "Rejected" },
      downloaded: { color: "bg-purple-500/20 text-purple-300", icon: Download, label: "Resume Downloaded" },
    };

    const config = statusConfig[status as keyof typeof statusConfig] || statusConfig.submitted;
    const Icon = config.icon;

    return (
      <Badge variant="secondary" className={config.color}>
        <Icon className="w-3 h-3 mr-1" />
        {config.label}
      </Badge>
    );
  };

  const formatDate = (dateString: string | Date) => {
    const date = typeof dateString === 'string' ? new Date(dateString) : dateString;
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const getApplicationStats = () => {
    if (!applications) return { total: 0, pending: 0, shortlisted: 0, rejected: 0 };

    return {
      total: applications.length,
      pending: applications.filter(app => ['submitted', 'reviewed', 'downloaded'].includes(app.status)).length,
      shortlisted: applications.filter(app => app.status === 'shortlisted').length,
      rejected: applications.filter(app => app.status === 'rejected').length,
    };
  };

  const stats = getApplicationStats();

  const ApplicationCard = ({ application }: { application: ApplicationWithJob }) => (
    <Card className="mb-4 bg-white/10 backdrop-blur-sm border-white/20">
      <CardHeader>
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <CardTitle className="text-white text-xl mb-2">{application.job.title}</CardTitle>
            <CardDescription className="text-gray-300">
              <div className="flex items-center gap-4 mb-2">
                <span className="flex items-center gap-1">
                  <MapPin className="w-4 h-4" />
                  {application.job.location}
                </span>
                <span className="flex items-center gap-1">
                  <Briefcase className="w-4 h-4" />
                  {application.job.type}
                </span>
                <span className="flex items-center gap-1">
                  <Calendar className="w-4 h-4" />
                  Applied {formatDate(application.appliedAt)}
                </span>
              </div>
            </CardDescription>
          </div>
          <div className="flex items-center gap-2">
            {getStatusBadge(application.status)}
          </div>
        </div>
      </CardHeader>
      <CardContent>
        <p className="text-gray-300 mb-3 line-clamp-2">{application.job.description}</p>

        {application.job.skills && application.job.skills.length > 0 && (
          <div className="flex flex-wrap gap-2 mb-3">
            {application.job.skills.map((skill, index) => (
              <Badge key={index} variant="outline" className="border-purple-400/30 text-purple-300">
                {skill}
              </Badge>
            ))}
          </div>
        )}

        {application.coverLetter && (
          <div className="mb-3 p-3 bg-white/5 rounded-lg">
            <Label className="text-white font-medium text-sm">Your Cover Letter</Label>
            <p className="text-gray-300 text-sm mt-1">{application.coverLetter}</p>
          </div>
        )}

        {application.notes && (
          <div className="mb-3 p-3 bg-blue-500/10 rounded-lg border-l-4 border-blue-400">
            <Label className="text-blue-400 font-medium text-sm">Recruiter Feedback</Label>
            <p className="text-gray-300 text-sm mt-1">{application.notes}</p>
          </div>
        )}

        <div className="flex items-center justify-between">
          <div className="flex items-center gap-4 text-sm text-gray-400">
            {application.lastViewedAt && (
              <span>Viewed: {formatDate(application.lastViewedAt)}</span>
            )}
            {application.downloadedAt && (
              <span>Resume Downloaded: {formatDate(application.downloadedAt)}</span>
            )}
          </div>

          {application.status === 'submitted' && (
            <Button
              onClick={() => handleWithdrawApplication(application.id)}
              variant="outline"
              size="sm"
              className="border-red-400/30 text-red-300 hover:bg-red-500/10"
            >
              <Trash2 className="w-4 h-4 mr-2" />
              Withdraw
            </Button>
          )}
        </div>
      </CardContent>
    </Card>
  );

  if (profileLoading || applicationsLoading) {
    return (
      <Layout>
        <div className="container mx-auto px-4 py-8">
          <div className="text-center py-8">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-400 mx-auto"></div>
            <p className="text-white mt-4">Loading dashboard...</p>
          </div>
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900">
        {/* Premium background effects */}
        <div className="absolute inset-0 bg-[url('data:image/svg+xml;base64,PHN2ZyB3aWR0aD0iMjAiIGhlaWdodD0iMjAiIHZpZXdCb3g9IjAgMCAyMCAyMCIgZmlsbD0ibm9uZSIgeG1sbnM9Imh0dHA6Ly93d3cudzMub3JnLzIwMDAvc3ZnIj48Y2lyY2xlIGN4PSIxIiBjeT0iMSIgcj0iMSIgZmlsbD0id2hpdGUiIGZpbGwtb3BhY2l0eT0iMC4wNSIvPjwvc3ZnPg==')] opacity-10"></div>
        <div className="absolute top-0 left-1/4 w-96 h-96 bg-purple-500/10 rounded-full blur-[100px] animate-pulse-slow"></div>
        <div className="absolute bottom-0 right-1/4 w-96 h-96 bg-blue-500/10 rounded-full blur-[100px] animate-pulse-slow" style={{ animationDelay: '1.2s' }}></div>

        <div className={`container mx-auto px-4 py-8 relative z-10 transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`}>
          <div className="max-w-6xl mx-auto">
            {/* Premium Header */}
            <div className="mb-12 pt-16">
              <div className="w-20 h-1.5 bg-gradient-to-r from-[#7B38FB] to-[#FF5BA8] rounded-full mb-6 animate-slide-right"></div>
              <div className="flex items-center gap-3 mb-4">
                <Target className="h-8 w-8 text-[#7B38FB]" />
                <h1 className="text-4xl md:text-5xl font-bold">
                  <span className="animate-gradient-text">Candidate</span>
                  <span className="text-white ml-3">Dashboard</span>
                </h1>
              </div>
              <p className="text-lg md:text-xl text-white/80 max-w-2xl leading-relaxed animate-slide-up" style={{ animationDelay: '0.3s' }}>
                Manage your profile and track your job applications with AI-powered insights
              </p>
            </div>

            {/* Statistics Cards */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
            <Card className="bg-white/10 backdrop-blur-sm border-white/20">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-gray-300 text-sm">Total Applications</p>
                    <p className="text-3xl font-bold text-blue-400">{stats.total}</p>
                  </div>
                  <Briefcase className="w-8 h-8 text-blue-400" />
                </div>
              </CardContent>
            </Card>

            <Card className="bg-white/10 backdrop-blur-sm border-white/20">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-gray-300 text-sm">In Progress</p>
                    <p className="text-3xl font-bold text-yellow-400">{stats.pending}</p>
                  </div>
                  <Clock className="w-8 h-8 text-yellow-400" />
                </div>
              </CardContent>
            </Card>

            <Card className="bg-white/10 backdrop-blur-sm border-white/20">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-gray-300 text-sm">Shortlisted</p>
                    <p className="text-3xl font-bold text-green-400">{stats.shortlisted}</p>
                  </div>
                  <CheckCircle className="w-8 h-8 text-green-400" />
                </div>
              </CardContent>
            </Card>

            <Card className="bg-white/10 backdrop-blur-sm border-white/20">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-gray-300 text-sm">Rejected</p>
                    <p className="text-3xl font-bold text-red-400">{stats.rejected}</p>
                  </div>
                  <XCircle className="w-8 h-8 text-red-400" />
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Main Content */}
          <Tabs defaultValue="profile" className="w-full">
            <TabsList className="grid w-full grid-cols-2 bg-white/10 border-white/20">
              <TabsTrigger value="profile">Profile</TabsTrigger>
              <TabsTrigger value="applications">My Applications ({stats.total})</TabsTrigger>
            </TabsList>

            <TabsContent value="profile" className="mt-6">
              <Card className="bg-white/10 backdrop-blur-sm border-white/20">
                <CardHeader>
                  <div className="flex items-center justify-between">
                    <div>
                      <CardTitle className="text-white text-2xl">Profile Information</CardTitle>
                      <CardDescription className="text-gray-300">
                        Manage your profile to auto-fill job applications
                      </CardDescription>
                    </div>
                    {!editingProfile && (
                      <Button onClick={handleEditProfile} variant="outline" className="border-white/20 text-white">
                        <Edit3 className="w-4 h-4 mr-2" />
                        Edit Profile
                      </Button>
                    )}
                  </div>
                </CardHeader>
                <CardContent>
                  {editingProfile ? (
                    <div className="space-y-6">
                      <div>
                        <Label htmlFor="bio" className="text-white">Bio</Label>
                        <Textarea
                          id="bio"
                          placeholder="Tell us about yourself..."
                          value={profileData.bio}
                          onChange={(e) => setProfileData({ ...profileData, bio: e.target.value })}
                          className="bg-white/5 border-white/20 text-white placeholder:text-gray-400"
                        />
                      </div>

                      <div>
                        <Label htmlFor="location" className="text-white">Location</Label>
                        <Input
                          id="location"
                          placeholder="City, Country"
                          value={profileData.location}
                          onChange={(e) => setProfileData({ ...profileData, location: e.target.value })}
                          className="bg-white/5 border-white/20 text-white placeholder:text-gray-400"
                        />
                      </div>

                      <div>
                        <Label htmlFor="linkedin" className="text-white">LinkedIn URL</Label>
                        <Input
                          id="linkedin"
                          placeholder="https://linkedin.com/in/yourprofile"
                          value={profileData.linkedin}
                          onChange={(e) => setProfileData({ ...profileData, linkedin: e.target.value })}
                          className="bg-white/5 border-white/20 text-white placeholder:text-gray-400"
                        />
                      </div>

                      <div>
                        <Label className="text-white">Skills</Label>
                        <div className="flex gap-2 mb-3">
                          <Input
                            placeholder="Add a skill"
                            value={newSkill}
                            onChange={(e) => setNewSkill(e.target.value)}
                            onKeyPress={(e) => e.key === 'Enter' && handleAddSkill()}
                            className="bg-white/5 border-white/20 text-white placeholder:text-gray-400"
                          />
                          <Button onClick={handleAddSkill} variant="outline" size="sm">
                            <Plus className="w-4 h-4" />
                          </Button>
                        </div>
                        <div className="flex flex-wrap gap-2">
                          {profileData.skills.map((skill, index) => (
                            <Badge key={index} variant="outline" className="border-purple-400/30 text-purple-300">
                              {skill}
                              <button
                                onClick={() => handleRemoveSkill(skill)}
                                className="ml-2 text-red-400 hover:text-red-300"
                              >
                                <X className="w-3 h-3" />
                              </button>
                            </Badge>
                          ))}
                        </div>
                      </div>

                      <div className="flex gap-3">
                        <Button
                          onClick={handleSaveProfile}
                          disabled={updateProfileMutation.isPending}
                          className="bg-purple-600 hover:bg-purple-700"
                        >
                          <Save className="w-4 h-4 mr-2" />
                          Save Profile
                        </Button>
                        <Button
                          onClick={() => setEditingProfile(false)}
                          variant="outline"
                          className="border-white/20 text-white"
                        >
                          Cancel
                        </Button>
                      </div>
                    </div>
                  ) : (
                    <div className="space-y-6">
                      {profile ? (
                        <>
                          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                            <div>
                              <Label className="text-white font-medium">Contact Information</Label>
                              <div className="mt-2 space-y-2">
                                <div className="flex items-center gap-2 text-gray-300">
                                  <Mail className="w-4 h-4" />
                                  <span>{user.username}</span>
                                </div>
                                {profile.location && (
                                  <div className="flex items-center gap-2 text-gray-300">
                                    <MapPin className="w-4 h-4" />
                                    <span>{profile.location}</span>
                                  </div>
                                )}
                                {profile.linkedin && (
                                  <div className="flex items-center gap-2 text-gray-300">
                                    <Linkedin className="w-4 h-4" />
                                    <a
                                      href={profile.linkedin}
                                      target="_blank"
                                      rel="noopener noreferrer"
                                      className="text-blue-400 hover:text-blue-300"
                                    >
                                      LinkedIn Profile
                                    </a>
                                  </div>
                                )}
                              </div>
                            </div>

                            {profile.skills && profile.skills.length > 0 && (
                              <div>
                                <Label className="text-white font-medium">Skills</Label>
                                <div className="flex flex-wrap gap-2 mt-2">
                                  {profile.skills.map((skill, index) => (
                                    <Badge key={index} variant="outline" className="border-purple-400/30 text-purple-300">
                                      {skill}
                                    </Badge>
                                  ))}
                                </div>
                              </div>
                            )}
                          </div>

                          {profile.bio && (
                            <div>
                              <Label className="text-white font-medium">Bio</Label>
                              <p className="text-gray-300 mt-2">{profile.bio}</p>
                            </div>
                          )}
                        </>
                      ) : (
                        <div className="text-center py-8">
                          <User className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                          <h3 className="text-xl font-semibold text-white mb-2">Complete Your Profile</h3>
                          <p className="text-gray-300 mb-4">
                            Add your information to auto-fill job applications and showcase your skills.
                          </p>
                          <Button onClick={handleEditProfile} className="bg-purple-600 hover:bg-purple-700">
                            Create Profile
                          </Button>
                        </div>
                      )}
                    </div>
                  )}
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="applications" className="mt-6">
              {applications && applications.length > 0 ? (
                applications.map(application => (
                  <ApplicationCard key={application.id} application={application} />
                ))
              ) : (
                <Card className="bg-white/10 backdrop-blur-sm border-white/20">
                  <CardContent className="p-8 text-center">
                    <Briefcase className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                    <h3 className="text-xl font-semibold text-white mb-2">No Applications Yet</h3>
                    <p className="text-gray-300 mb-4">
                      Start applying to jobs to track your progress here.
                    </p>
                    <Button className="bg-purple-600 hover:bg-purple-700">
                      Browse Jobs
                    </Button>
                  </CardContent>
                </Card>
              )}
            </TabsContent>
          </Tabs>
          </div>
        </div>
      </div>
    </Layout>
  );
}

client/src/pages/job-analytics-dashboard.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/use-auth";
import { Redirect } from "wouter";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import Layout from "@/components/Layout";
import { Eye, MousePointer, TrendingUp, Briefcase, Calendar, MapPin, Search, Filter } from "lucide-react";
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, LineChart, Line, PieChart, Pie, Cell } from "recharts";
import { format } from "date-fns";

interface JobWithAnalytics {
  id: number;
  title: string;
  location: string;
  type: string;
  description: string;
  skills: string[];
  deadline: string;
  createdAt: string;
  isActive: boolean;
  status: string;
  postedBy: number;
  postedByUser: {
    id: number;
    firstName: string;
    lastName: string;
    username: string;
  };
  analytics: {
    views: number;
    applyClicks: number;
    conversionRate: string;
  };
}

export default function JobAnalyticsDashboard() {
  const { user } = useAuth();
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const [selectedTab, setSelectedTab] = useState("overview");

  // Redirect if not authorized
  if (!user || (user.role !== 'admin' && user.role !== 'recruiter')) {
    return <Redirect to="/auth" />;
  }

  const { data: jobsWithAnalytics, isLoading } = useQuery<JobWithAnalytics[]>({
    queryKey: ['/api/analytics/jobs'],
  });

  // Filter jobs based on search and status
  const filteredJobs = jobsWithAnalytics?.filter(job => {
    const matchesSearch = !searchTerm || 
      job.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      job.location.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesStatus = statusFilter === "all" || 
      (statusFilter === "active" && job.isActive) ||
      (statusFilter === "inactive" && !job.isActive) ||
      job.status === statusFilter;

    return matchesSearch && matchesStatus;
  }) || [];

  // Calculate aggregate statistics
  const totalViews = filteredJobs.reduce((sum, job) => sum + job.analytics.views, 0);
  const totalApplyClicks = filteredJobs.reduce((sum, job) => sum + job.analytics.applyClicks, 0);
  const averageConversionRate = filteredJobs.length > 0 
    ? (filteredJobs.reduce((sum, job) => sum + parseFloat(job.analytics.conversionRate), 0) / filteredJobs.length).toFixed(2)
    : "0.00";

  // Prepare chart data
  const chartData = filteredJobs.map(job => ({
    name: job.title.length > 20 ? job.title.substring(0, 20) + "..." : job.title,
    views: job.analytics.views,
    applyClicks: job.analytics.applyClicks,
    conversionRate: parseFloat(job.analytics.conversionRate),
  }));

  const performanceData = filteredJobs.map(job => ({
    name: job.title.length > 15 ? job.title.substring(0, 15) + "..." : job.title,
    performance: parseFloat(job.analytics.conversionRate),
  }));

  const statusData = [
    { name: 'Active', value: filteredJobs.filter(job => job.isActive).length, color: '#10b981' },
    { name: 'Inactive', value: filteredJobs.filter(job => !job.isActive).length, color: '#ef4444' },
    { name: 'Pending', value: filteredJobs.filter(job => job.status === 'pending').length, color: '#f59e0b' },
  ];

  const getStatusBadge = (job: JobWithAnalytics) => {
    if (!job.isActive) return <Badge className="bg-red-500/20 text-red-300">Inactive</Badge>;
    if (job.status === 'pending') return <Badge className="bg-yellow-500/20 text-yellow-300">Pending</Badge>;
    if (job.status === 'approved') return <Badge className="bg-green-500/20 text-green-300">Active</Badge>;
    return <Badge className="bg-gray-500/20 text-gray-300">Unknown</Badge>;
  };

  const getPerformanceColor = (conversionRate: number) => {
    if (conversionRate >= 10) return "text-green-400";
    if (conversionRate >= 5) return "text-yellow-400";
    return "text-red-400";
  };

  if (isLoading) {
    return (
      <Layout>
        <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 p-8">
          <div className="flex items-center justify-center min-h-screen">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-400"></div>
          </div>
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 p-8">
        <div className="max-w-7xl mx-auto">
          {/* Header */}
          <div className="mb-8">
            <h1 className="text-4xl font-bold text-white mb-2">Job Analytics Dashboard</h1>
            <p className="text-gray-300">Track performance metrics and insights for your job listings</p>
          </div>

          {/* Quick Stats */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
            <Card className="bg-white/10 backdrop-blur-sm border-white/20">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-gray-300 text-sm">Total Jobs</p>
                    <p className="text-3xl font-bold text-white">{filteredJobs.length}</p>
                  </div>
                  <Briefcase className="w-8 h-8 text-blue-400" />
                </div>
              </CardContent>
            </Card>

            <Card className="bg-white/10 backdrop-blur-sm border-white/20">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-gray-300 text-sm">Total Views</p>
                    <p className="text-3xl font-bold text-white">{totalViews.toLocaleString()}</p>
                  </div>
                  <Eye className="w-8 h-8 text-green-400" />
                </div>
              </CardContent>
            </Card>

            <Card className="bg-white/10 backdrop-blur-sm border-white/20">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-gray-300 text-sm">Apply Clicks</p>
                    <p className="text-3xl font-bold text-white">{totalApplyClicks.toLocaleString()}</p>
                  </div>
                  <MousePointer className="w-8 h-8 text-purple-400" />
                </div>
              </CardContent>
            </Card>

            <Card className="bg-white/10 backdrop-blur-sm border-white/20">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-gray-300 text-sm">Avg Conversion</p>
                    <p className="text-3xl font-bold text-white">{averageConversionRate}%</p>
                  </div>
                  <TrendingUp className="w-8 h-8 text-orange-400" />
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Filters */}
          <Card className="mb-8 bg-white/10 backdrop-blur-sm border-white/20">
            <CardContent className="p-6">
              <div className="flex flex-col md:flex-row gap-4">
                <div className="flex-1">
                  <div className="relative">
                    <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
                    <Input
                      placeholder="Search jobs..."
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                      className="pl-10 bg-slate-800/50 border-slate-600 text-white"
                    />
                  </div>
                </div>
                <Select value={statusFilter} onValueChange={setStatusFilter}>
                  <SelectTrigger className="w-48 bg-slate-800/50 border-slate-600 text-white">
                    <SelectValue placeholder="Filter by status" />
                  </SelectTrigger>
                  <SelectContent className="bg-slate-800 border-slate-600">
                    <SelectItem value="all">All Status</SelectItem>
                    <SelectItem value="active">Active</SelectItem>
                    <SelectItem value="inactive">Inactive</SelectItem>
                    <SelectItem value="pending">Pending</SelectItem>
                    <SelectItem value="approved">Approved</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </CardContent>
          </Card>

          {/* Analytics Tabs */}
          <Tabs value={selectedTab} onValueChange={setSelectedTab} className="w-full">
            <TabsList className="grid w-full grid-cols-3 bg-white/10 border-white/20">
              <TabsTrigger value="overview" className="data-[state=active]:bg-purple-500/20 data-[state=active]:text-purple-300">
                Overview
              </TabsTrigger>
              <TabsTrigger value="performance" className="data-[state=active]:bg-purple-500/20 data-[state=active]:text-purple-300">
                Performance
              </TabsTrigger>
              <TabsTrigger value="detailed" className="data-[state=active]:bg-purple-500/20 data-[state=active]:text-purple-300">
                Detailed View
              </TabsTrigger>
            </TabsList>

            <TabsContent value="overview" className="mt-6">
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                {/* Views vs Apply Clicks Chart */}
                <Card className="bg-white/10 backdrop-blur-sm border-white/20">
                  <CardHeader>
                    <CardTitle className="text-white">Views vs Apply Clicks</CardTitle>
                    <CardDescription className="text-gray-300">
                      Comparison of job views and application clicks
                    </CardDescription>
                  </CardHeader>
                  <CardContent>
                    <ResponsiveContainer width="100%" height={300}>
                      <BarChart data={chartData}>
                        <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
                        <XAxis dataKey="name" stroke="#9CA3AF" />
                        <YAxis stroke="#9CA3AF" />
                        <Tooltip 
                          contentStyle={{ 
                            backgroundColor: '#1F2937', 
                            border: '1px solid #374151',
                            borderRadius: '8px',
                            color: '#F9FAFB'
                          }} 
                        />
                        <Bar dataKey="views" fill="#10b981" name="Views" />
                        <Bar dataKey="applyClicks" fill="#8b5cf6" name="Apply Clicks" />
                      </BarChart>
                    </ResponsiveContainer>
                  </CardContent>
                </Card>

                {/* Job Status Distribution */}
                <Card className="bg-white/10 backdrop-blur-sm border-white/20">
                  <CardHeader>
                    <CardTitle className="text-white">Job Status Distribution</CardTitle>
                    <CardDescription className="text-gray-300">
                      Current status breakdown of all jobs
                    </CardDescription>
                  </CardHeader>
                  <CardContent>
                    <ResponsiveContainer width="100%" height={300}>
                      <PieChart>
                        <Pie
                          data={statusData}
                          cx="50%"
                          cy="50%"
                          labelLine={false}
                          label={({ name, value }) => `${name}: ${value}`}
                          outerRadius={100}
                          fill="#8884d8"
                          dataKey="value"
                        >
                          {statusData.map((entry, index) => (
                            <Cell key={`cell-${index}`} fill={entry.color} />
                          ))}
                        </Pie>
                        <Tooltip 
                          contentStyle={{ 
                            backgroundColor: '#1F2937', 
                            border: '1px solid #374151',
                            borderRadius: '8px',
                            color: '#F9FAFB'
                          }} 
                        />
                      </PieChart>
                    </ResponsiveContainer>
                  </CardContent>
                </Card>
              </div>
            </TabsContent>

            <TabsContent value="performance" className="mt-6">
              <Card className="bg-white/10 backdrop-blur-sm border-white/20">
                <CardHeader>
                  <CardTitle className="text-white">Conversion Rate Performance</CardTitle>
                  <CardDescription className="text-gray-300">
                    Track how well your jobs convert views to applications
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <ResponsiveContainer width="100%" height={400}>
                    <LineChart data={performanceData}>
                      <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
                      <XAxis dataKey="name" stroke="#9CA3AF" />
                      <YAxis stroke="#9CA3AF" />
                      <Tooltip 
                        contentStyle={{ 
                          backgroundColor: '#1F2937', 
                          border: '1px solid #374151',
                          borderRadius: '8px',
                          color: '#F9FAFB'
                        }} 
                      />
                      <Line 
                        type="monotone" 
                        dataKey="performance" 
                        stroke="#f59e0b" 
                        strokeWidth={3}
                        dot={{ fill: '#f59e0b' }}
                        name="Conversion Rate (%)"
                      />
                    </LineChart>
                  </ResponsiveContainer>
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="detailed" className="mt-6">
              <div className="space-y-6">
                {filteredJobs.map(job => (
                  <Card key={job.id} className="bg-white/10 backdrop-blur-sm border-white/20">
                    <CardContent className="p-6">
                      <div className="flex justify-between items-start mb-4">
                        <div className="flex-1">
                          <h3 className="text-xl font-semibold text-white mb-2">{job.title}</h3>
                          <div className="flex items-center gap-4 mb-2 text-sm text-gray-300">
                            <span className="flex items-center gap-1">
                              <MapPin className="h-4 w-4" />
                              {job.location}
                            </span>
                            <span className="flex items-center gap-1">
                              <Calendar className="h-4 w-4" />
                              {format(new Date(job.createdAt), "MMM d, yyyy")}
                            </span>
                            <Badge variant="outline" className="border-purple-400/30 text-purple-300">
                              {job.type}
                            </Badge>
                          </div>
                          <div className="flex items-center gap-2 mb-3">
                            {getStatusBadge(job)}
                            <span className="text-sm text-gray-400">
                              Posted by: {job.postedByUser.firstName} {job.postedByUser.lastName}
                            </span>
                          </div>
                        </div>
                      </div>

                      {/* Analytics Metrics */}
                      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 p-4 bg-slate-800/30 rounded-lg">
                        <div className="text-center">
                          <div className="flex items-center justify-center gap-2 mb-1">
                            <Eye className="h-4 w-4 text-green-400" />
                            <span className="text-sm font-medium text-gray-300">Views</span>
                          </div>
                          <p className="text-2xl font-bold text-green-400">{job.analytics.views}</p>
                        </div>
                        <div className="text-center">
                          <div className="flex items-center justify-center gap-2 mb-1">
                            <MousePointer className="h-4 w-4 text-purple-400" />
                            <span className="text-sm font-medium text-gray-300">Apply Clicks</span>
                          </div>
                          <p className="text-2xl font-bold text-purple-400">{job.analytics.applyClicks}</p>
                        </div>
                        <div className="text-center">
                          <div className="flex items-center justify-center gap-2 mb-1">
                            <TrendingUp className="h-4 w-4 text-orange-400" />
                            <span className="text-sm font-medium text-gray-300">Conversion Rate</span>
                          </div>
                          <p className={`text-2xl font-bold ${getPerformanceColor(parseFloat(job.analytics.conversionRate))}`}>
                            {job.analytics.conversionRate}%
                          </p>
                        </div>
                      </div>

                      {/* Job Skills */}
                      {job.skills && job.skills.length > 0 && (
                        <div className="mt-4">
                          <p className="text-sm font-medium text-gray-300 mb-2">Required Skills:</p>
                          <div className="flex flex-wrap gap-2">
                            {job.skills.map((skill, index) => (
                              <Badge key={index} variant="outline" className="border-slate-600 text-slate-300">
                                {skill}
                              </Badge>
                            ))}
                          </div>
                        </div>
                      )}
                    </CardContent>
                  </Card>
                ))}
              </div>
            </TabsContent>
          </Tabs>
        </div>
      </div>
    </Layout>
  );
}

client/src/pages/job-details-page-fixed..content:
import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useRoute } from "wouter";
import { MapPin, Clock, Calendar, Users, FileText, Upload, Briefcase, Star } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { Job, insertApplicationSchema } from "@shared/schema";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { z } from "zod";
import Layout from "@/components/Layout";

export default function JobDetailsPage() {
  const [match, params] = useRoute("/jobs/:id");
  const { toast } = useToast();
  const [showApplicationForm, setShowApplicationForm] = useState(false);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    coverLetter: "",
  });
  const [resumeFile, setResumeFile] = useState<File | null>(null);
  const [isVisible, setIsVisible] = useState(false);

  const jobId = params?.id ? parseInt(params.id) : null;

  // Fade-in animation on mount
  useEffect(() => {
    const timer = setTimeout(() => setIsVisible(true), 200);
    return () => clearTimeout(timer);
  }, []);

  const { data: job, isLoading, error } = useQuery<Job>({
    queryKey: ["/api/jobs", jobId],
    queryFn: async () => {
      const response = await fetch(`/api/jobs/${jobId}`);
      if (!response.ok) throw new Error("Failed to fetch job");
      return response.json();
    },
    enabled: !!jobId,
  });

  const applicationMutation = useMutation({
    mutationFn: async (data: FormData) => {
      const response = await fetch(`/api/jobs/${jobId}/apply`, {
        method: "POST",
        body: data,
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || "Failed to submit application");
      }
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Application submitted successfully",
        description: "We'll review your application and get back to you soon.",
      });
      setShowApplicationForm(false);
      setFormData({ name: "", email: "", phone: "", coverLetter: "" });
      setResumeFile(null);
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to submit application",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!resumeFile) {
      toast({
        title: "Resume required",
        description: "Please upload your resume to continue.",
        variant: "destructive",
      });
      return;
    }

    try {
      const validatedData = insertApplicationSchema.parse({
        ...formData,
        jobId: jobId!,
      });

      const formDataToSend = new FormData();
      Object.entries(validatedData).forEach(([key, value]) => {
        if (value !== undefined && value !== null) {
          formDataToSend.append(key, value.toString());
        }
      });

      if (resumeFile) {
        formDataToSend.append('resume', resumeFile);
      }

      applicationMutation.mutate(formDataToSend);
    } catch (error) {
      if (error instanceof z.ZodError) {
        toast({
          title: "Validation error",
          description: error.errors[0].message,
          variant: "destructive",
        });
      }
    }
  };

  const formatDate = (dateString: string | Date) => {
    const date = typeof dateString === 'string' ? new Date(dateString) : dateString;
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  if (!match || !jobId) {
    return (
      <Layout>
        <div className="flex items-center justify-center min-h-[60vh]">
          <div className="text-center text-white">
            <h1 className="text-2xl font-bold mb-2">Job Not Found</h1>
            <p>The job you're looking for doesn't exist.</p>
          </div>
        </div>
      </Layout>
    );
  }

  if (isLoading) {
    return (
      <Layout>
        <div className="flex items-center justify-center min-h-[60vh]">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-400 mx-auto"></div>
            <p className="text-white mt-4">Loading job details...</p>
          </div>
        </div>
      </Layout>
    );
  }

  if (error || !job) {
    return (
      <Layout>
        <div className="flex items-center justify-center min-h-[60vh]">
          <div className="text-center text-white">
            <h1 className="text-2xl font-bold mb-2">Error</h1>
            <p>Failed to load job details. Please try again.</p>
          </div>
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900">
        {/* Premium background effects */}
